    cin >> P[i];
  }
  sort( P, P + M, greater< int >());

  for(int i = 0; i < N; i++){
    cin >> C[i] >> E[i];
  }

  int dp[50001]; //dp[i]:饅頭をi個以上入れるための箱の値段の最小値
