int main(){
	cin >> m >> n;
	for (int i = 0; i < m; i++)
		cin >> p[i];
	for (int i = 0; i < n; i++)
		cin >> box[i][0] >> box[i][1];
	sort(p, p + m, greater<int>());
	for (int i = 0; i < n; i++){
		for (int j = 0; j < m; j++){
			if (j >= box[i][0]-1){
				int a;
				if (i)
					a = dp[i - 1][j - box[i][0]] - box[i][1];
				else
					a = box[i][1] * -1;
				for (int k = j - box[i][0] + 1; k <= j; k++){
					a += p[k];
					dp[i][k] = max(max(dp[i][k], dp[i - 1][k]), max(a, dp[i][k - 1]));
				}
			}
		}
	}
	cout << dp[n - 1][m - 1] << endl;
	return 0;
