int dp(int i, int j)
{
	if (i == n || j >= m) return 0;
	if (memo[i][j] != -1) return memo[i][j];
	int res;
	int sum = s[j + c[i]] - s[j];
	if (sum <= e[i]) res = dp(i + 1, j);
	else
	{
		res = max(dp(i + 1, j), dp(i + 1, j + c[i]) + sum - e[i]);
	}
	return memo[i][j] = res;
}

