int P[10000];
int C[10000],E[10000];
int dp[501][10001];

int min( int x,int y ){
	if( x < y )
		return x;
	return y;
}
int max( int x,int y ){
	if( x > y )
		return x;
	return y;
}

int compare( const void *s1,const void *s2 ){
	return *(int*)s2 - *(int*)s1;
}

int main(){
	int i,j;
	int ans = 0;
	int rui = 0;

	scanf("%d%d",&M,&N);
	for( i = 0;i < M;i++ )
		scanf("%d",P+i);
	for( i = 0;i < N;i++ )
		scanf("%d%d",C+i,E+i);

	for( i = 0;i <= N;i++ ){
		for( j = 0;j <= M;j++ ){
			dp[i][j] = 1000000000;
		}
	}

	dp[0][0] = 0;
	for( i = 0;i < N;i++ ){
		for( j = 0;j <= M;j++ ){
			dp[i+1][min(j+C[i],M)] = min( dp[i+1][min(j+C[i],M)],dp[i][j]+E[i] );
			dp[i+1][j] = min( dp[i+1][j],dp[i][j] );
		}
	}

	qsort(P,M,sizeof(int),compare);
	for( i = 0;i <= M;i++ ){
		ans = max( ans,rui-dp[N][i] );
		rui += P[i];
	}
	printf("%d\n",ans);

	return 0;
}

