int m, n, p[10001], c[501], e[501], dp[10001][501], ans = 0, price = 0;
int main() {
	cin >> m >> n;
	for(int i = 1; i <= m; i++){
		cin >> p[i];
	}
	for(int i = 1; i <= n; i++){
		cin >> c[i] >> e[i];
	}
	for(int i = 0; i < n; i++){
		fill(dp[i], dp[i] + 501, INF);
	}

	sort(p + 1, p + m, greater<int>());

	for(int i = 1; i <= n; i++){
		for(int j = 1; j <= m; j++){
			if(c[i] >= j){
				dp[i][j] = min(dp[i - 1][j], e[i]);
			} else{
				dp[i][j] = min(dp[i - 1][j], dp[i][j - 1] + dp[i][j - 2]);
			}
		}
	}

	for(int i = 1; i <= m; i++){
		price += p[i];
		ans = max(ans, price - dp[n][i]);
	}
	if(ans < 0){
		ans = 0;
	}

	cout << ans << endl;
	return 0;
}

