}

//詰め終わった箱の数、詰めた饅頭の数でDPする。箱には{詰めない,残りの饅頭すべて詰める,満タンにする}の
//3つのパターンで入れるとよい。(入れた饅頭の個数が同じの時、高いのから入れてれば、饅頭の売り上げは一緒.)
//要するに箱代の最小化問題だ。饅頭は高いのから入れたほうがいいに決まっている。饅頭は箱に詰めるならたくさん
//詰めたほうがいいに決まっている。証明は知らないけど、自明だろう。
#include<iostream>
#include<algorithm>
#include<functional>
using namespace std;

int M,N,P[10000],C[500],E[500];
int DP[501][10001];	//箱代でDPする

int main(){
	cin >> M >> N;
	for(int i = 0;i < M;i++){
		cin >> P[i];
	}
	for(int i = 0;i < N;i++){
		cin >> C[i] >> E[i];
	}
	for(int i = 0;i <= N;i++){
		for(int j = 0;j <= M;j++){
			DP[i][j] = 1000000000;
		}
	}
	DP[0][0] = 0;
	//DPする(小問題+初期値(箱代)に分けられる)
	for(int i = 0;i < N;i++){
		for(int j = 0;j <= M;j++){
			int num;
			num = j+C[i];
			if(num > M)num = M;
			//代入
			if(DP[i+1][j] > DP[i][j])
				DP[i+1][j] = DP[i][j];
			if(DP[i+1][num] > DP[i][j]+E[i])
				DP[i+1][num] = DP[i][j]+E[i];
		}
	}
	sort(P,P+M,greater<int>());
	//貪欲法
	int max_le = 0,sum = 0;
	for(int i = 0;i <= M;i++){
		if(max_le < sum-DP[N][i])
			max_le = sum-DP[N][i];
		sum += P[i];
	}
	cout << max_le << endl;
	return 0;
}

