const int INF = 1145141919;
int m, n, p[10001], c[501], e[501], dp[2][10001], ans = 0, price = 0, capacity = 0;
int main() {
    cin >> m >> n;
    for(int i = 0; i < m; i++){
        cin >> p[i];
    }
    for(int i = 0; i < n; i++){
        cin >> c[i] >> e[i];
    }
    for(int i = 0; i < 2; i++){
        fill(dp[i], dp[i] + 10001, INF);
    }

    sort(p, p + m, greater<int>());

    for(int i = 0; i < n; i++){
        capacity += c[i];
        for(int j = 0; j < m; j++){
            if(c[i] > j){
                dp[i % 2][j] = min(dp[(i % 2)? 0 : 1][j], e[i]);
            } else if(capacity > j){
                dp[i % 2][j] = min(dp[i % 2][j], dp[(i % 2)? 0 : 1][j - c[i]] + e[i]);
            } else{
                break;
            }
        }
    }

    for(int i = 0; i < m; i++){
        price += p[i];
        ans = max(ans, price - dp[(n - 1) % 2][i]);
    }

    cout << ans << endl;
    return 0;
}

