typedef pair<int,int> PII;

int N,M;
int P[10010];
PII C[550];
int V[10010];
int mem[10010][550];

int cal(int n,int kin){
    int res=0;
    if(mem[n][kin]!=0)return mem[n][kin];
    if(n==N||kin>M)return 0;
    if(N-n>=C[kin].second)res=max(cal(n+C[kin].second,kin+1)+V[n+C[kin].second]-V[n]-C[kin].first,max(cal(n,kin+1),res));
    else res=max(cal(N,kin+1)+V[N]-V[n]-C[kin].first,max(cal(n,kin+1),res));
    return mem[n][kin]=res;
}


