const int maxn = 506;
const int maxm = 20000 + 123;
int n , m;
int p[maxm];
int cap[maxn] , cost[maxn];

void ReadData() {
    scanf("%d%d" , &m , &n); // cakes and boxed
    For(i , 1 , m) scanf("%d" , &p[i]);
    For(i , 1 , n) scanf("%d%d" , &cap[i] , &cost[i]);
}

int dp[maxn][maxm];

bool maximize(int &a , const int &b) {
    if (a < b) {a = b; return true;} return false;
}

bool minimize(int &a , const int &b) {
    if (a > b) {a = b; return true;} return false;
}

void Update(int pos , int sum , int val) {
    if (dp[pos][sum] == -1) dp[pos][sum] = val; else minimize(dp[pos][sum] , val);
}

void Process() {
    memset(dp , -1 , sizeof(dp));
    dp[0][0] = 0;
    For(i , 0 , n - 1) For(sum , 0 , 20000) if (dp[i][sum] != -1) {
        Update(i + 1 , sum , dp[i][sum]);
        if (sum + cap[i] <= 20000) Update(i + 1 , sum + cap[i + 1] , dp[i][sum] + cost[i + 1]);
    }

    Ford(sum , 19999 , 0) if (dp[n][sum + 1] != -1) Update(n , sum , dp[n][sum + 1]);

    sort(p + 1 , p + 1 + m); reverse(p + 1 , p + 1 + m);
    long long res = 0;
    long long collect = 0;
    For(i , 1 , m) {
        collect += p[i];
        if (dp[n][i] != -1) {
            res = max(res , collect - dp[n][i]);
        } 
    }
    cout << res << endl;
    //For(i , 1 , m) cout << p[i] << endl; 
}

