#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
ll dp[2][11111];
int N,M;
ll P[11111];
ll C[555];
ll E[555];
ll K[11111];
#define INF (11111111111LL)
ll solve(){
  int id = 0;
  fill(dp[0],dp[2],INF);
  dp[0][0] = 0;
  for(int i=0;i<N;i++){
    id = i&1;
    for(int j=0;j<=M;j++){
      int nd = id^1;
      if( dp[id][j] == INF ) continue;
      //      printf("%d %d  =  %lld\n",i,j,dp[id][j]);
      ll m = min((ll)M,j+C[i]);
      dp[nd][m] = min(dp[nd][m],
                      dp[id][j] + E[i]);
      dp[nd][j] = min(dp[nd][j], dp[id][j]);
    }
  }
  id=N&1;
  K[M] = dp[id][M];
  for(int i=M-1;i>-1;i--){
    // cout << K[i+1] << endl;
    K[i] = min(K[i+1],dp[id][i]);
  }
  sort(P,P+M);
  ll res = 0;
  ll sum = 0;
  for(int i=M-1;i>-1;i--){
    sum += P[i];
    int nums = M-i;
    ll ri = sum - K[nums];
    res = max(ri,res);
  }

  return res;

}

int main(){
  cin >> M >> N;
  for(int i=0;i<M;i++){
    cin >> P[i];
  }
  for(int i=0;i<N;i++){
    cin >> C[i] >> E[i];
  }
  cout << solve() << endl;
}

