#define REP(i,n) for(int i=0;i<(int)(n);i++)


const int NMAX = 1000, MMAX = 20000, INF = 1e9;
vector<int> cookies;
vector<pair<int, int> > boxes; // first: capacity, second: cost
int dp[MMAX];
int N, M;

void init() {
  REP(i, M + 100) {
    dp[i] = INF;
  }

  dp[0] = 0;
}

void solve() {
  init();

  for (pair<int, int> box : boxes) {
    int capacity = box.first, cost = box.second; 
//    cout << "[" << capacity << " " << cost << "]" << endl;
    for (int m = M; m >= 1; m--) {
      if (m >= capacity) {
        dp[m] = min(dp[m], dp[m - capacity] + cost);
      } else {
        dp[m] = min(dp[m], cost);
      }

//      REP(i, 15) {
  //      cout << dp[i] << " ";
    //  }
      //cout << endl;
    }
  }
}

int maximum() {
  int ans = 0;

  sort(cookies.begin(), cookies.end(), greater<int>());
  cookies.insert(cookies.begin(), 0);

  for (int i = 1, len = cookies.size(); i < len; i++) {
    cookies[i] = cookies[i - 1] + cookies[i];
  }

  for (int i = 0, len = cookies.size(); i < len; i++) {
    ans = max(ans, cookies[i] - dp[i]);
  }

  return ans;
}

int main() {
  cin >> M >> N;
  REP(i, M) {
    int p;
    cin >> p;
    cookies.push_back(p);
  }

  REP(i, N) {
    int c, e;
    cin >> c >> e;
    boxes.push_back(make_pair(c, e));
  }

  solve();

  cout << maximum() << endl;
}

