	rep( i, N ) rep( j, M+1 ) rep( k, C[i]+1 ) if( j+k <= M )
		dp[i+1][j+k] = std::min( dp[i+1][j+k], dp[i][j]+(k?E[i]:0) ); 

	int ans = 0, sum = 0;
	rep( j, M+1 )
	{
		ans = std::max( ans, sum-dp[N][j] );

		if( j < M )
			sum += P[j];
	}

	printf( "%d\n", ans );

	return 0;
}

