using namespace std;
struct P {
	long long size, cost;
};
long long dp[600][10500];
long long a[10500],m,n;
long long d[10500];
P b[600];
long long solve(int i, int j) {
	if (i == n) return 0;
	long long sum = 0;
	if (j + b[i].size < m) {
		if (dp[i + 1][j + b[i].size] != -1) {
			sum = (d[j + b[i].size] - d[j] - b[i].cost) +
				dp[i + 1][j + b[i].size];
		}
		else {
			sum = (d[j + b[i].size] - d[j] - b[i].cost) +
				(dp[i + 1][j + b[i].size] =
				solve(i + 1, (j + b[i].size)));
		}
	}
	else {
		if (dp[i + 1][m] != -1) {
			sum = d[m] - d[j] - b[i].cost + dp[i + 1][m];
		}
		else {
			sum = d[m] - d[j] - b[i].cost + (dp[i + 1][m] =
				solve(i + 1, m));
		}
	}
	if (dp[i + 1][j] != -1) {
		sum = max(sum, dp[i + 1][j]);
	}
	else {
		sum = max(sum, dp[i+1][j]=solve(i + 1, j));
	}
	return sum;
}
