int solve(int now, int nokori){
    if(nokori <= 0) return 0;
    if(now == N) return 0;
    if(dp[now][nokori] != INT_MIN) return dp[now][nokori];
    int re = 0;
    if(Psum[M - nokori] - Psum[M - nokori + CE[now].first] > CE[now].second){
