static const int MAX_M = 10000;
static const int MAX_N = 500;

int M, N;
int P[MAX_M + 1];
int C[MAX_N + 1], E[MAX_N + 1];
int memo[MAX_N + 1][MAX_M + 1];

int main(){
	scanf("%d %d", &M, &N);
	for(int i = 1; i < M + 1; i++) scanf("%d", &P[i]);
	for(int i = 1; i < N + 1; i++) scanf("%d %d", &C[i], &E[i]);

	fill(memo[0], memo[N + 1], 1 << 30);
	sort(P, P + M + 1);
	reverse(P + 1, P + M + 1);
	for(int i = 1; i < M + 1; i++) P[i] += P[i - 1];
	for(int i = 1; i < N + 1; i++) C[i] += C[i - 1];

	for(int i = 1; i < N + 1; i++){
		for(int j = 0; j < min(M + 1, C[i] + 1); j++){
			if(j == 0) memo[i][j] = 0;
			else if(j < C[i] - C[i - 1]) memo[i][j] = min(memo[i - 1][j], E[i]);
			else memo[i][j] = min(memo[i - 1][j], memo[i - 1][j - (C[i] - C[i - 1])] + E[i]);
		}
	}
	int res = 0;
	for(int i = 0; i < M + 1; i++){
		if(memo[N][i] == 1 << 30) break;
		res = max(res, P[i] - memo[N][i]);
	}
	printf("%d\n", res);
	return 0;
}

