using namespace std;

const int INF = 123456789;

int m, n;
int p[10000];
int c[500], e[500];

int dp[10001];
int ndp[10001];

int main(void)
{
	scanf("%d %d",&m,&n);
	for (int i = 0; i < m; ++i)scanf("%d", p + i);
	for (int j = 0; j < n; ++j)scanf("%d %d", c + j, e + j);
	sort(p, p + m, [](int a, int b) {return a > b; });
	for (int i = 1; i < m; ++i)p[i] += p[i - 1];
	fill(dp, dp + m + 1, INF);
	fill(ndp, ndp + m + 1, INF);
	dp[0] = 0;
	for (int j = 0; j < n; ++j)
	{
		int cap = c[j]; int exp = e[j];
		for (int i = 0; i <= m; ++i)
		{
			ndp[i] = dp[i];
			if (i - cap >= 0)
			{
				ndp[i] = min(ndp[i], dp[i - cap] + exp);
			}
		}
		for (int i = m; i >= 0; --i)
		{
			if (i != 0 && ndp[i - 1] > ndp[i])
			{
				ndp[i - 1] = ndp[i];
			}
			dp[i] = ndp[i];
		}
	}
	int answer = 0;
	for (int i = 1; i <= m; ++i)
	{
		answer = max(answer, p[i - 1] - dp[i]);
	}
	printf("%d\n", answer);
	return 0;
}

