using namespace std;

bool LT(double a,double b) { return !equals(a,b) && a < b; }
bool LTE(double a,double b) { return equals(a,b) || a < b; }

struct Data {
  int cap,cost;
  bool operator < ( const Data &data ) const {
    double temp1 = double(cost)/double(cap);
    double temp2 = double(data.cost)/double(data.cap);
    if( !equals(temp1,temp2) ) return LT(temp1,temp2);
    if( cost != data.cost ) return cost < data.cost;
    return cap < data.cap;
  }
};

int main(){
  int M,N;
  cin >> M >> N;
  vector<int> manj(M);
  rep(i,M) cin >> manj[i];
  vector<Data> cas(M);
