  sort(manj.rbegin(),manj.rend());
  sort(cas.begin(),cas.end());

  vector<int> sum(M+1),case_sum(N+1),case_cap(N+1);
  sum[0] = case_sum[0] = case_cap[0] = 0;
  rep(i,M) sum[i+1] = sum[i] + manj[i];
  rep(i,N) case_sum[i+1] = case_sum[i] + cas[i].cost,
           case_cap[i+1] = case_cap[i] + cas[i].cap;
  int maxi = 0;
  rep(i,N) {
    int capa = case_cap[i+1];
    int sale = sum[min(capa,M)];
    int cost = case_sum[i+1];
    maxi = max(maxi,sale-cost);
  }
  cout << maxi << endl;
  return 0;
}

