int dfs(int n, int m){
  if(n>=N) return P[min(m,M)];
  int &res = dp[n][m];
  if(res >= 0) return res;
  return res = max(dfs(n+1,m), dfs(n+1,min(m+C[n],M))-E[n]);
}

