struct Box {
	int num, price;
	Box() = default;
	Box(int _num, int _price) : num(_num), price(_price) {}
};

bool operator<(const Box& lhs, const Box& rhs) {
	return double(lhs.price) / double(lhs.num) <
		double(rhs.price) / double(rhs.num);
}

