int solve(int next_boxes,int manju_c) {
	int ans=0, res;
	for (int i = next_boxes; i < n; ++i) {
		if (manju_c + boxes[i].num > m) {
			res = std::max(manju[m-1] - ((manju_c == 0)? 0 : manju[manju_c - 1]) - boxes[i].price,
				solve(i + 1, manju_c));
			if (res > 0)
				ans += res;
			break;
		}
		else {
			res = manju[manju_c + boxes[i].num - 1] - ((manju_c==0)? 0 : manju[manju_c - 1]) - boxes[i].price;
			if (res < 0)
				break;
			ans += res;
			manju_c += boxes[i].num;
		}
	}
	return ans;
	std::cout << ans << std::endl;
}

int main() {
	std::cin >> m >> n;
	for (int i = 0; i < m; ++i)
		std::cin >> manju[i];
	for (int i = 0; i < n; ++i)
		std::cin >> boxes[i].num >> boxes[i].price;
	std::sort(manju, manju + m, std::greater<int>());
	for (int i = 1; i < m; ++i)
		manju[i] += manju[i - 1];
	std::sort(boxes, boxes + n);

	std::cout << solve(0,0) << std::endl;
	return 0;
}

