#define INF 1000000000000
#define MAX(a,b) ((a)>(b)?(a):(b))
#define MIN(a,b) ((a)<(b)?(a):(b))

typedef pair<long long,long long> P;

int main()
{
    int M, N;
    cin >> M >> N;

    vector<long long> man;
    vector<P> pack;

    for(int i=0; i<M; ++i) {
        long long temp;
        cin >> temp;
        man.push_back(temp);
    }

    sort(man.rbegin(),man.rend());

    for(int i=1; i<M; ++i) {
        man[i]+=man[i-1];
    }

    for(int i=0; i<N; ++i) {
        long long c,e;
        cin >> c >> e;
        pack.push_back(make_pair(e,c));
    }

    sort(pack.begin(),pack.end());

    long long dp[2][M];

    for(int j=0; j<M; ++j) {
        dp[0][j]=INF;
        dp[1][j]=INF;
    }

    for(int i=0; i<M; ++i) {
        if(i<pack[0].second) dp[0][i]=pack[0].first;
    }

    for(int i=1; i<N; ++i) {
        for(int j=0; j<M; ++j) {
            if(j<pack[i].second) {
                dp[i%2][j]=MIN(dp[i%2][j],dp[(i-1)%2][j]);
                dp[i%2][j]=MIN(dp[i%2][j],pack[i].first);
            }
            if(j+pack[i].second<M) {
                dp[i%2][j+pack[i].second]=MIN(dp[(i-1)%2][j+pack[i].second],dp[(i-1)%2][j]+pack[i].first);
            }
        }
    }

    long long ans=0;
    for(int i=0; i<M; ++i) {
        ans=MAX(ans,man[i]-dp[(N-1)%2][i]);
    }

    cout << ans << endl;



    return 0;
}

