	cin >> m >> n;
	n2 = n;
	for (int i = 0; i < m; i++) cin >> a[i];
	for (int i = 0; i < n; i++) cin >> b[i].max >> b[i].cost;
	sort(a, a + m);
	reverse(a, a + m);
	int sum = 0;
	int e = 0;
	while (m!=0) {
		long double sum2 = 1000000000;
		int sum4 = 0, f = 0;
		for (int i = 0; i < n2; i++) {
			if (!b[i].used) {
