int main(){
	for(int i = 0; i < 501; i++) for(int j = 0; j < 10001; j++) dp[i][j] = -1;
	for(int i = 0; i < 501; i++) dp[i][0] = 0;
	scanf("%d%d", &m, &n);
	p[0] = 0;
	for(int i = 0; i < m; i++) scanf("%d", &p[i+1]);
	for(int i = 0; i < n; i++) scanf("%d%d", &c[i], &e[i]);
	sort(p+1,p+m+1,greater<int>());
	for(int i = 1; i <= m; i++) p[i] += p[i-1];
	for(int i = 0; i < n; i++){
		for(int j = 0; j <= m; j++){
			if(dp[i][j] == -1) continue;
			if(j+c[i] > m) break;
			dp[i+1][j+c[i]] = dp[i][j]+e[i];
		}
		for(int j = m-1; j >= 0; j--){
			if(dp[i+1][j] == -1) dp[i+1][j] = dp[i+1][j+1];
