#include <bits/stdc++.h>

using namespace std;

int M, N;
vector<int> Price;
vector<pair<int, int> > BoxInfo;
int DpTotalOfBox[500][10001];
const int INF = 1000000000;

void solve() {
	int ans, curSum, curPrice;

	memset(DpTotalOfBox, 0, sizeof(DpTotalOfBox));
	for (int i = 0; i < N; ++i) {
		for (int j = 1; j <= M; ++j) {
			DpTotalOfBox[i][j] = INF;
		}
	}

	ans = 0;
	curPrice = BoxInfo[0].second;
	curSum = min(M, BoxInfo[0].first);
	for (int i = 1; i <= curSum; ++i) {
		DpTotalOfBox[0][i] = curPrice;
		ans = max(ans, Price[i - 1] - DpTotalOfBox[0][i]);
	}

	for (int i = 1; i < N; ++i) {
		curPrice = BoxInfo[i].second;
		curSum = BoxInfo[i].first;
		for (int j = 1; j <= M; ++j) {
			DpTotalOfBox[i][j] = min(DpTotalOfBox[i - 1][j], curPrice + DpTotalOfBox[i - 1][max(0, j - curSum)]);
			ans = max(ans, Price[j - 1] - DpTotalOfBox[i][j]);
		}
	}

	cout << ans << endl;
}

int main() {
	int p, sum;

	cin >> M >> N;
	for (int i = 0; i < M; ++i) {
		cin >> p;
		Price.push_back(p);
	}
	for (int i = 0; i < N; ++i) {
		cin >> sum >> p;
		BoxInfo.push_back({ sum,p });
	}
	sort(Price.begin(), Price.end(), greater<int>());
	for (int i = 1; i < M; ++i) {
		Price[i] += Price[i - 1];
	}
	solve();

	return 0;
}

