	return ans;
}
int dp(int h, int ma)
{
	if (h >= n)return 0;
	if (ma >= m)return 0;
	if (ma + hako[h].l >= m)
	{
	}
	else memo[ma + hako[h].l] = min(memo[ma + hako[h].l], memo[ma] + hako[h].m);

	dp(h + 1, ma);
	dp(h + 1, ma + hako[h].l);
	return 0;



}

