int solve( int now, int box ) {
	if ( now == m || box == n ) {
		return 0;
	}
	if ( dp[ now ][ box ] >= 0 ) {
		return dp[ now ][ box ];
	}
	int res = 0;
	for ( int i = 0; i <= _box[ box ].second; i++ ) {
