#include <algorithm>
using namespace std;
const int INF = 1e9;

int main(){
  int N, M;
  cin >> M >> N;
  vector<int> P(M), C(N), E(N);
  for(int i = 0; i < M; ++i){
    cin >> P[i];
  }
  int C_sum = 0;
  for(int i = 0; i < N; ++i){
    cin >> C[i] >> E[i];
    C_sum += C[i];
  }
  vector<int> DP(C_sum + 1, INF);
  DP[0] = 0;
  for(int i = 0; i < N; ++i){
    vector<int> DP_ = DP;
    for(int j = 0; j <= C_sum - C[i]; ++j){
      if(DP[j] >= INF) continue;
      DP_[j + C[i]] = min(DP_[j + C[i]], DP[j] + E[i]);
    }
    DP = DP_;
  }
  for(int i = C_sum-1; i >= 0; --i) DP[i] = min(DP[i], DP[i+1]);
  sort(P.begin(), P.end(), greater<int>());
  int s = 0, ans = 0;
  for(int i = 0; i < min(M, C_sum); ++i){
    s += P[i];
    ans = max(ans, s - DP[i+1]);
  }
  cout << ans << endl;
  return 0;
}

