int M, N;
int P[10001];
int C[501], E[501];
int dp[501][10001];

void solve(){
  for(int i = 0; i < 501; i++)
    fill(dp[i], dp[i] + 10001, INT_MAX);

  dp[0][0] = 0;

  for(int i = 0; i < N; i++){
    for(int j = 0; j <= M; j++){
      if(dp[i][j] == INT_MAX) continue;

      int nj = min(M, j + C[i]);
      dp[i + 1][j] = min(dp[i + 1][j], dp[i][j]);
      dp[i + 1][nj] = min(dp[i + 1][nj], dp[i][j] + E[i]);
    }
  }

  sort(P, P + M, greater<int>());

  int res = 0;
  int sum = accumulate(P, P + M, 0);
  int box = INT_MAX;

  for(int i = M; i > 0; i--){
    box = min(box, dp[N][i]);
    if(box != INT_MAX){
      res = max(res, sum - box);
    }
    sum -= P[i - 1];
  }

  cout << res << endl;
}

int main(void){
  while(cin >> M >> N){
    for(int i = 0; i < M; i++){
      cin >> P[i];
    }
    for(int i = 0; i < N; i++){
      cin >> C[i] >> E[i];
    }
    solve();
  }
}

