const int INF = 1 << 28;

int m, n;
int p[10010], c[510], e[510];
int dp[510][10010];

int calc(int i, int j)
{
	if (j <= 0) return 0;
	if (i == n) return INF;
	if (~dp[i][j]) return dp[i][j];
	return dp[i][j] = min(calc(i + 1, j), e[i] + calc(i + 1, j - c[i]));
}

int main()
{
	cin >> m >> n;

	for (int i = 0; i < m; i++){
		cin >> p[i];
