		cin >> c[i] >> e[i];
	}

	memset(dp, -1, sizeof(dp));
	int res = 0, s = 0;
	for (int i = 0; i < m; i++){
		s += p[i];
		res = max(res, s - calc(0, i + 1));
	}

	cout << res << endl;
}

