typedef long long ll;

int M,N;
int P[10005], C[512], E[512];
ll dp[10005][512];

ll dfs(int m, int n){
  if(m>M || n>=N) return 0;
  ll res = 0;
  if(m<=M) res = max(res, dfs(min(M, m+C[n]), n+1) + P[min(M,m+C[n])]-P[m] - E[n]);
  res = max(res, dfs(m, n+1));
  return res;
}

int main(){
  scanf("%d%d",&M,&N);
  P[0] = 0;
  rep(i,M) scanf("%d",P+i+1);
  sort(P+1, P+M+1, greater<int>());
  rep(i,M) P[i+1] += P[i];
  rep(i,N) scanf("%d%d", C+i,E+i);
  memset(dp, -1, sizeof(dp));
  printf("%lld\n", dfs(0, 0));
}

