#include <iostream>
#include <algorithm>

using namespace std;
int M,N;
int P[10001];
int C[501];
int E[501];
int MaxM=0;
int TP[10001]={};
const int INF=200000000;
int dp[10001][501]={};

int calc(int a,int b){
	int ans=INF;
	if(b<0)return ans;
	if(dp[a][b]<INF)return dp[a][b];
	ans=calc(a,b-1);
	if(a<=C[b]){
		ans=min(ans,E[b]);
	}else{
		ans=min(ans,calc(a-C[b],b-1)+E[b]);
	}

	dp[a][b]=ans;
	return ans;
}
int main() {
	cin>>M>>N;
	for(int i=0;i<M;++i)cin>>P[i];
	for(int i=0;i<N;++i)cin>>C[i]>>E[i];

	sort(P,P+M);
	TP[1]=P[M-1];
	for(int i=2;i<M+1;++i){
		TP[i]=TP[i-1]+P[M-i];
	}

	for(int i=0;i<N;++i)MaxM+=C[i];
	MaxM=min(MaxM,M);

	fill(dp[0],dp[10001],INF);
	int R=0;
	for(int i=1;i<MaxM+1;++i){
		R=max(R,TP[i]-calc(i,N));
	}
	cout<<R<<endl;
	return 0;
}

