#include <bits/stdc++.h>
using namespace std;

int m, n;
int p[101010];
int c[505], e[505];

int dp[512][10101];

int dfs(int i = 0, int sum = 0, int cost = 0)
{
  if (i == n){
    return 0;
  }
  int &ret = dp[i][sum];
  if (ret != -1) return ret;

  int v = min(sum + c[i], m);
  ret = max(dfs(i + 1, v, cost + e[i]) + p[v] - p[sum] - e[i], dfs(i + 1, sum, cost));
  ret = max(0, ret);

  return ret;
}

int main()
{
  memset(dp, -1, sizeof(dp));
  cin >> m >> n;
  for (int i = 1; i <= m; i++){
    cin >> p[i];
  }
  sort(p + 1, p + m + 1, greater<int>());
  for (int i = 1; i <= m; i++){
    p[i] += p[i - 1];
  }

  for (int i = 0; i < n; i++){
    cin >> c[i] >> e[i];
  }

  cout << dfs() << endl;
}

