#include<iostream>
#include<vector>
#include<algorithm>
#include<string.h>
using namespace std;

struct hg
{
	int l;
	int m;
};


int m, n;
vector<int> man;
vector<hg> hako;
int memo[30000];

int check(int w, int i)
{
	int ans = 0;
	if (w - 1 < 0)ans = man[w + i-1];
	else ans = man[w + i-1] - man[w-1];
	return ans;
}
int dp(int h, int ma)
{
	if (h >= n)return 0;
	memo[ma] = min(memo[max(0,ma - hako[h].l)] + hako[h].m,memo[ma]);
	if (ma  == 0)dp(h + 1, m);else dp(h, ma-1);
	return 0;
}

int main()
{
	cin >> m >> n;
	memset(memo, -1, sizeof(memo));
	for (int i = 0; i < m; i++)
	{
		int x;
		cin >> x;
		man.push_back(x);
	}
	for (int i = 0; i < 30000; i++)memo[i] = 9999999999999;
	sort(man.rbegin(), man.rend());
	for (int i = 1; i < m; i++)
	{
		man[i] += man[i - 1];
	}
	for (int i = 0; i < n; i++)
	{
		hg o;
		cin >> o.l >> o.m;
		hako.push_back(o);
	}
	int ans = 0;
	memo[0] = 0;
	dp(0, m);
	for (int i = 1; i <= m; i++)
	{
		ans = max(-1 *( memo[i] - man[i-1]), ans);
	}
	cout << ans << endl;

}

