		sort(a, a + m);
		reverse(a, a + m);
		for (int i = 0; i < n; i++)
			cin >> b[i].size >> b[i].cost;
		for (int i = 0; i < m; i++) {
			for (int j = 0; j < n; j++)
				dp[i][j] = -1;
		}
		for (int i = 1; i <= m; i++) {
			d[i] = d[i - 1] + a[i - 1];
		}
		long long sum = solve(0, 0);
		if (sum < 0) sum = 0;
		cout << sum << endl;
