int memo[1001][10001];

int check(int w, int i)
{
	int ans = 0;
	for (int s = w; s < w + i; s++)ans += man[s];
	return ans;
}
int dp(int h, int w)
{
	if (h == n)return 0;
	if (w >= m)return 0;
	if (memo[h][w] != -1)return memo[h][w];
	int ans = 0;
	for (int i = 0; i <= min(m - w,hako[h].l); i++)
	{
		if (i == 0)
		{
			ans = max(dp(h + 1, w), ans);
		}
		else
		{
			ans = max(dp(h + 1, w + i) - (hako[h].m - check(w, i)), ans);
		}
	}

	return memo[h][w] = ans;



}

