// IOI饅頭 http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=0599
#include <bits/stdc++.h>
using namespace std;

int M, N;
vector<int> manjus;
vector<pair<int, int>> boxes;
int mem[10001][501];

bool boxcmp(const pair<int, int> a, const pair<int, int> b) {
    if((a.second * b.first) == (b.second * a.first)) {
        return a.first > b.first;
    } else {
        return (a.second * b.first) < (b.second * a.first);
    }
}

int mouke(int m, int b) {
    if(mem[m][b] >= 0) return mem[m][b];
    if(m >= M || b >= N){
        return 0;
    }
    int manju_nokori = M - m;
    int box_size = boxes[b].first;
    int shushi = boxes[b].second * (-1);

    if(manju_nokori < box_size) {
        for(int i = m; i < M; i++) {
            shushi += manjus[i];
        }
        if(shushi > 0) {
            int result = max(mouke(m, b + 1), shushi);
            mem[m][b] = result;
            return result;
        } else {
            int result = mouke(m, b + 1);
            mem[m][b] = result;
            return result;
        }
    } else {
        for(int i = m; i - m < box_size; i++) {
            shushi += manjus[i];
        }
        if(shushi > 0) {
            int result = max(mouke(m, b + 1), shushi + mouke(m + box_size, b + 1));
            mem[m][b] = result;
            return result;
        } else {
            int result = max(mouke(m, b + 1), 0);
            mem[m][b] = result;
            return result;
        }
    }
}

int main() {
    for(int i = 0; i <= 10000; i++) {
        for(int j = 0; j <= 500; j++) {
            mem[i][j] = -1;
        }
    }
    cin >> M >> N;
    for(int i = 0; i < M; i++) {
        int Pi;
        cin >> Pi;
        manjus.push_back(Pi);
    }
    for(int i = 0; i < N; i++) {
        int Cj, Ej;
        cin >> Cj >> Ej;
        boxes.push_back(make_pair(Cj, Ej));
    }
    sort(manjus.begin(), manjus.end(), greater<int>());
    sort(boxes.begin(), boxes.end(), boxcmp);
    cout << mouke(0, 0) << endl;
    return 0;
}

