using namespace std;

struct Manjyu{
	Manjyu(int arg_price){
		price = arg_price;
	}
	bool operator<(const struct Manjyu &arg) const{
		return price < arg.price;
	};
	int price;
};

struct Box{

	Box(int arg_price,int arg_num_contain,double arg_per_manjyu){
		price = arg_price;
		num_contain = arg_num_contain;
		per_manjyu = arg_per_manjyu;
	}
	bool operator<(const struct Box &arg) const{
		return per_manjyu > arg.per_manjyu;
	};
	int price,num_contain;
	double per_manjyu;
};


int main(){

	int M,N;
	scanf("%d %d",&M,&N);
	int table[M+1];

	priority_queue<Manjyu> M_Q;
	int tmp;
	for(int i = 0; i < M; i++){
		scanf("%d",&tmp);
		M_Q.push(Manjyu(tmp));
	}

	table[0] = 0;
	table[1] = M_Q.top().price;
	M_Q.pop();
	for(int i = 2; i <= M; i++){
		table[i] = M_Q.top().price;
		table[i] += table[i-1];
		M_Q.pop();
	}

	int dp[M+1];
	dp[0] = 0;
	for(int i = 1; i <= M; i++)dp[i] = BIG_NUM;

	int num,price;

	for(int loop = 0; loop < N; loop++){
		scanf("%d %d",&num,&price);

		if(num > M){
			dp[M] = min(dp[M],price);
		}else{
			for(int i = M; i-num >= 0; i--){
				if(dp[i-num] != BIG_NUM){
					dp[i] = min(dp[i],dp[i-num]+price);
				}
			}
		}
		for(int i = M-1; i >= 0; i--){
			dp[i] = min(dp[i],dp[i+1]);
		}
	}

	int maximum = 0;
	for(int i = 1; i <= M; i++){
		maximum = max(maximum,table[i]-dp[i]);
	}

	printf("%d\n",maximum);

	return 0;
}

