const int INF = 1001001001;

int M, N;
int P[10000], C[500], E[500];
int dp[500][10001];

int main(){
    scanf("%d %d", &M, &N);

    for(int i=0;i<M;i++){
        scanf("%d", P+i);
    }
    std::sort(P, P+M);
    std::reverse(P, P+M);

    for(int i=1;i<M;i++){
        P[i] += P[i-1];
    }

    for(int i=0;i<N;i++){
        scanf("%d %d", C+i, E+i);
    }

    for(int i=0;i<=M;i++){
        if(i == 0){dp[0][i] = 0;}
        else if(i <= C[0]){dp[0][i] = E[0];}
        else{dp[0][i] = INF;}
    }

    for(int i=1;i<N;i++){
        for(int j=M;j>=0;j--){
            dp[i][j] = dp[i-1][j];
            if(j < M){dp[i][j] = std::min(dp[i][j], dp[i][j+1]);}
            if(j - C[i] >= 0){
                dp[i][j] = std::min(dp[i][j], dp[i-1][j-C[i]] + E[i]);
            }
        }
    }

    // for(int i=0;i<N;i++){
    //     for(int j=0;j<=M;j++){
    //         printf("%d ", dp[i][j]);
    //     }
    //     puts("");
    // }

    int res = 0;
    for(int i=0;i<=M;i++){
        // printf("%d: %d, %d\n", i, i>0?P[i-1]:0, dp[N-1][i]);
        res = std::max(res, (i>0?P[i-1]:0) - dp[N-1][i]);
    }

    printf("%d\n", res);
}

