int main()
{
	int N, M;

	scanf("%d", &N);
	scanf("%d", &M);

	vector<int> P(N);
	vector<int> C(M);
	vector<int> E(M);

	for (int i = 0; i < N; i++)
	{
		scanf("%d", &P[i]);
	}

	for (int i = 0; i < M; i++)
	{
		scanf("%d", &C[i]);
		scanf("%d", &E[i]);
	}

	vector<vector<int> > dp(M + 1, vector<int>(N + 1, INF)); dp[0][0] = 0;

	for (int i = 1; i <= M; i++)
	{
		for (int j = 0; j <= N; j++)
		{
			for (int k = max(0, j - C[i]); k <= j; k++)
			{
				dp[i][j] = min(dp[i][j], dp[i - 1][k] + E[i]);
			}
		}
	}

	sort(P.begin(), P.end(), greater<int>());

	vector<int> S(N + 1); S[0] = 0;

	for (int i = 1; i <= N; i++)
	{
		S[i] = S[i - 1] + P[i - 1];
	}

	int ret = 0;

	for (int i = 0; i <= N; i++)
	{
		ret = max(ret, S[i] - dp[M][i]);
	}

