#define TEMP_T template<typename T>
TEMP_T void sort(T& v){ sort(v.begin(), v.end()); }
TEMP_T void revs(T& v){ reverse(v.begin(), v.end()); }
TEMP_T void uniq(T& v){ sort(v); v.erase(unique(v.begin(), v.end()), v.end()); }
TEMP_T T    cums(T& v){ T r(v.size()); partial_sum(v.begin(), v.end(), r); return r; }
TEMP_T void show(T& v, char delim=' ', char end='\n'){ for(int i=0; i<v.size()-1; i++) cout << v[i] << delim; cout << v[v.size()-1] << end; }
TEMP_T T    acum(vector<T>& v, T b=0){ return accumulate(v.begin(), v.end(), b); }
TEMP_T vector<T> inpt(int n){ vector<T> v(n); for (int i=0; i<n; i++) cin >> v[i]; return v; }

static inline int in(){ int x; scanf("%d", &x); return x; }
static const int dx[] = {0, 0, 1, -1, 1, 1, -1, -1};
static const int dy[] = {1, -1, 0, 0, 1, -1, 1, -1};

struct Box {
  int cap;
  int val;
  Box(int cap, int val) : cap(cap), val(val) {}
};

int main()
{
  int n, m;
  m = in();
  n = in();
  vector<int> p = inpt<int>(m);
  sort(p);
  revs(p);
  vector<Box> bs;
  for (int i = 0; i < n; i++){
    int c, v;
    c = in();
    v = in();
    bs.push_back(Box(c, v));
  }
  int dp[10002]; // dp[cap] = min cost;
  const int inf = 100000000;
  fill(dp, dp + m + 2, inf);
  dp[0] = 0;
  for (int i = 0; i < n; i++){
    for (int j = m; j >= 0; j--){
      if (j + bs[i].cap > m) dp[m] = min(dp[m], dp[j] + bs[i].val);
      else dp[j + bs[i].cap] = min(dp[j + bs[i].cap], dp[j] + bs[i].val);
      dp[j] = min(dp[j], dp[j + 1]);
    }
  }
  vector<int> cum(m);
  cum[0] = p[0];
  for (int i = 1; i < m; i++){
    cum[i] = cum[i - 1] + p[i];
  }
  int maxi = 0;
  for (int i = 0; i < m; i++){
    maxi = max(maxi, cum[i] - dp[i + 1]);
  }
  printf("%d\n", maxi);
  return (0);
}

