#include <cstdio>
#include <algorithm>
#include <cstring>

using namespace std;

const int MAX_M=10000,MAX_N=500;

int M,N;
int P[MAX_M];
int C[MAX_N],E[MAX_N];

int sum[MAX_M];	//sum[i]=(iまでのまんじゅうを選んだ最大の価値)
int memo[MAX_N][MAX_M];
int dfs(int,int);

int main(){
	int i;
	scanf("%d %d",&M,&N);
	for (i=0;i<M;i++){
		scanf("%d",P+i);
	}
	for (i=0;i<N;i++){
		scanf("%d %d",C+i,E+i);
	}
	sort(P,P+M);
	sum[0]=P[M-1];
	for (i=1;i<M;i++){
		sum[i]=sum[i-1]+P[M-1-i];
	}
	memset(memo,-1,sizeof(memo));
	printf("%d\n",dfs(0,0));
	return 0;
}

int dfs(int i,int j){
	//iの箱を使うかどうか選択
	//j以下のまんじゅうを使う
	if (i==N || j==M){
		return 0;
	}
	if (memo[i][j]!=-1){
		return memo[i][j];
	}
	int res=dfs(i+1,j);	//箱を使わない場合
	//箱を使う場合
	//まんじゅうの個数
	int c=min(C[i],M-j);	//箱の限界orまんじゅうの限界
	int e=sum[j+c-1]-(j>0 ? sum[j-1] : 0);	//まんじゅうの価値
	res=max(res,e-E[i]+dfs(i+1,j+c));
	return memo[i][j]=res;
}

