	{
		dp[i+1][j] = std::min( dp[i+1][j], dp[i][j] );

		dp[i+1][std::min(M,j+C[i])] = std::min( dp[i+1][std::min(M,j+C[i])], dp[i][j]+E[i] ); 
	}

	int ans = 0, sum = 0;
	rep( j, M+1 )
	{
		ans = std::max( ans, sum-dp[N][j] );

		if( j < M )
			sum += P[j];
	}

	printf( "%d\n", ans );

	return 0;
}

#include <cstdio>
