#include <stdio.h>
#include <stdlib.h>

int qsc(const void *x,const void *y) {
	int a=*((int*)x);
	int b=*((int*)y);
	if(a>b)return -1;
	if(a<b)return 1;
	return 0;
}

int M,N;
int P[10001];
int C[500];
int E[500];

int memo[10010][510];

int search(int man,int box) {
	int ret=0,retc;
	int i;
	if(box>=N || man>M)return 0;
	if(memo[man][box]>0)return memo[man][box]-1;
	/* hachuu sinai */
	ret=search(man,box+1);
	/* hachuu suru */
	for(i=1;i<=C[box] && man+i<=M+1;i++) {
		retc=search(man+i,box+1)+(P[man+i-1]-P[man-1])-E[box];
		if(retc>ret)ret=retc;
	}
	memo[man][box]=ret+1;
	return ret;
}

int main(void) {
	int i;
	if(scanf("%d%d",&M,&N)!=2)return 1;
	for(i=1;i<=M;i++) {
		if(scanf("%d",&P[i])!=1)return 1;
	}
	for(i=0;i<N;i++) {
		if(scanf("%d%d",&C[i],&E[i])!=2)return 1;
	}
	qsort(P+1,M,sizeof(P[0]),qsc);
	for(i=1;i<=M;i++)P[i]+=P[i-1];
	printf("%d\n",search(1,0));
	return 0;
}

