static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public BigInteger nextBigInteger(){return new BigInteger(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

import java.io.*;
import java.math.*;
import java.util.*;

class Main{
	public static void main(String...args) throws IOException{
		Scanner r = new Scanner(System.in);
		int M,N;
		M=Integer.parseInt(r.next());
		N=Integer.parseInt(r.next());
		int[] man = new int[M];
		int[] kazu = new int[N];
		int[] hako = new int[N];
		int[] value = new int[N];
		int tmp,index;
		int manin,hakoin;
		int total;
		hakoin=total=0;
		for(int i=0;i<M;i++){
			man[i]=Integer.parseInt(r.next());
		}
		for(int i=0;i<N;i++){
			kazu[i]=Integer.parseInt(r.next());
			hako[i]=Integer.parseInt(r.next());
			value[i]=hako[i]/kazu[i];
		}
		Arrays.sort(man);
		for(int i=0;i<N-1;i++){
			tmp=value[i];
			index=i;
			for(int j=i+1;j<N;j++){
				if(tmp>value[j]){
					tmp=value[j];
					index=j;
				}
			}
			value[index]=value[i];
			value[i]=tmp;
			tmp=kazu[index];
			kazu[index]=kazu[i];
			kazu[i]=tmp;
			tmp=hako[index];
			hako[index]=hako[i];
			hako[i]=tmp;
		}
		int flag=0;
		int ans;
		total=0;
		manin=M-1;
		while(true){
			ans=0;
			if(hakoin == N ) break;
			if(man[manin]>value[hakoin]){
				for(int i=0;i<kazu[hakoin];i++){
					if(-i+manin == -1){
						flag=1;
						break;
					}
					ans+=man[-i+manin];
				}
				if(ans-hako[hakoin]>0){
					total+=ans-hako[hakoin];
					hakoin++;
					manin-=kazu[hakoin-1];
				}
				else flag=1;
			}
			else break;
			if(flag==1)break;
		}
		System.out.println(total);
	}
}

