public class hello
{
    public static void Main()
    {
        string[] line = Console.ReadLine().Trim().Split(' ');
        var m = int.Parse(line[0]);
        var n = int.Parse(line[1]);
        var p = new int[m];
        var c = new int[n];
        var e = new int[n];
        for (int i = 0; i < m; i++) p[i] = int.Parse(Console.ReadLine().Trim());
        Array.Sort(p);
        Array.Reverse(p);
        for (int i = 0; i < n; i++)
        {
            line = Console.ReadLine().Trim().Split(' ');
            c[i] = int.Parse(line[0]);
            e[i] = int.Parse(line[1]);
        }
        var dp = getDP(c, e, m, n);
        var ans = getAns(dp, p, m, n);
        Console.WriteLine(ans);
    }
    static int getAns(int[,] dp, int[] p, int m, int n)
    {
        var ans = 0;
        var dpL = dp.GetLength(1);
        var imax = Min(m, dpL - 1);
        var msum = 0;
        for (int i = 0; i < imax; i++)
        {
            msum += p[i];
            var t = msum - dp[n, i + 1];
            ans = Max(ans, t);
        }
        return ans;
    }
    static int[,] getDP(int[] c, int[] e, int m, int n)
    {
        var mmax = c.Sum();
        var dpmax = Min(mmax, m);
        var dp = new int[n + 1, dpmax + 1];
        for (int i = 0; i < n + 1; i++)
            for (int j = 1; j < dpmax + 1; j++) dp[i, j] = int.MaxValue;
        var jmax = 0;
        for (int i = 1; i <= n; i++)
        {
            jmax += c[i - 1];
            jmax = Min(jmax, dpmax );
            for (int j = 1; j <= jmax; j++)
            {
                var w = dp[i - 1, Max(0, j - c[i - 1])] + e[i - 1];
                dp[i, j] = Min(dp[i - 1, j], w);
            }
        }
        return dp;
    }
}

