import scala.annotation.tailrec
import net.pushl.io.MyConsole

// Document: http://www.scala-lang.org/api/current/#package
// -----------------------------------------------------------------------------
class Solver(val stdio: MyConsole){
  import stdio._ // shadow Console.~
                 // val diff = List((-1,0),(1,0),(0,-1),(0,1))
  val diff = (for(i <- -1 to 1;
                  j <- -1 to 1;
                  if i != 0 || j != 0) yield (i,j)).toList

  def go(fil: Array[Array[Option[Int]]], will_remove: List[(Int,Int)]) : Int =
    if(will_remove.length == 0)
      0
    else
      1 + go(fil, goaux(fil, will_remove))

  def goaux(fil: Array[Array[Option[Int]]], will_remove: List[(Int,Int)]) : List[(Int,Int)] = {
    val he = fil.length
    val we = fil(0).length
    will_remove match {
      case Nil    => Nil
      case h :: t => h match {
        case (r,c) => {
          val inner = diff.map({case (dy,dx) => (r+dy,c+dx)}).filter({case (y,x) => 0 <= y && y < he && 0 <= x && x < we})
          val out   = inner.map({case (i,j) => {
                                   fil(i)(j) match {
                                     case None    => None
                                     case Some(k) => {
                                       if(k == 1){
                                         fil(i)(j) = None
                                         Some((i,j))
                                       }else{
                                         fil(i)(j) = Some(k-1)
                                         None
                                       }
                                     }
                                   }
                                 }}).collect({case Some(k) => k})
          out ++ goaux(fil,t)
        }
      }
    }
  }
  def main() : Unit = {
    val h,w = nextInt
    val fil = (1 to h).map(_ => readLine.map(c =>
                             if(c == '.') None
                             else         Some(c - '0')))

    val copy = (fil map (_.toArray)).toArray
    val rem  = (fil.zipWithIndex).flatMap({case (row,i) =>
                                            row.zipWithIndex.collect({case (content,j) if content == None =>
                                                                       (i,j)})}).toList
    println(go(copy,rem)-1)
  }
}









