    if(will_remove.length == 0)
      ans
    else
      go(fil, goaux(fil, will_remove), 1 + ans)

  def goaux(fil: Array[Array[Option[Int]]], will_remove: List[(Int,Int)]) : List[(Int,Int)] = {
