          val out   = inner.map({case (i,j) => {
                                   fil(i)(j) match {
                                     case None    => None
                                     case Some(k) => {
                                       if(k == 1){
                                         fil(i)(j) = None
                                         Some((i,j))
                                       }else{
                                         fil(i)(j) = Some(k-1)
                                         None
                                       }
                                     }
                                   }
                                 }}).collect({case Some(k) => k})
          out ++ goaux(fil,t)
        }
      }
    }
  }
  def main() : Unit = {
    val h,w = nextInt
    val fil = (1 to h).map(_ => readLine.map(c =>
                             if(c == '.') None
                             else         Some(c - '0')))

    val copy = (fil map (_.toArray)).toArray
    val rem  = (fil.zipWithIndex).flatMap({case (row,i) =>
                                            row.zipWithIndex.collect({case (content,j) if content == None =>
                                                                       (i,j)})}).toList
    println(go(copy,rem)-1)
  }
}









